import React from 'react'
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBCard, MDBInput } from 'mdbreact';
import "./login.css";
import axios from 'axios';
import {Link} from 'react-router-dom'
import { toast,ToastContainer } from 'react-toastify';

toast.configure();

class Login extends React.Component{
   constructor(props){
     super(props);

     this.state={
       loginOpen:true,
       username:'',
       password:'',
       InvalideUser:false,
       toastId:null
     }
     this.changeState = this.changeState.bind(this) 
   }

   onChangeusername=(event)=>{
     this.setState({username:event.target.value})
   }
   onChangepassword=(event)=>{
     this.setState({password:event.target.value})
   }

   changeState(){
     const User = {
       username:this.state.username,
       password:this.state.password
     }

     axios.request({
      method: "post", 
      url: "http://localhost:5000/api/users/login", 
      data: User, 
      onUploadProgress: p => {
        const progress = p.loaded / p.total;

        // check if we already displayed a toast
        if(this.state.toastId.current === null){
            this.state.toastId = toast('Upload in Progress', {
            progress: progress
          });
        } else {
          toast.update(this.state.toastId.current, {
            progress: progress
          })
        }
      }
    }).then(data => {
      // Upload is done! 
      // The remaining progress bar will be filled up
      // The toast will be closed when the transition end
      toast.done(toastId.current);
    })
   }




 render(){
  return(
    <div>
      {this.state.loginOpen?(
        <div>

      <div className="Card">
      <MDBContainer>
        <MDBRow>
          <MDBCol md='6'>
                <MDBCard
                  className='card-image'
                   style={{
                     backgroundImage:
                       'url(https://i.pinimg.com/originals/4f/8a/18/4f8a18a2a6f9a3291bee46c24ea9ec5d.jpg)',
                     width: '25rem', 
                     backgroundRepeat: 'no-repeat',
                     backgroundSize: '100%',
                   }}
                >

            <div className='text-white rgba-blue-slight py-5 px-5 z-depth-4'>
                        <div className='text-center'>
                           <h3 className='text-dark mb-5 mt-4 font-weight-bold'>
                              <strong>Log</strong>
                               <a className='purple-text font-weight-bold'>
                               <strong>In</strong>
                               </a>
                           </h3>
                        </div>
                      <div>

                      <MDBInput
                        label='Your Username'
                        onChange={this.onChangeusername}
                        icon='user'
                        group
                        type='text'
                        validate
                        labelClass='text-dark font-weight-bold'
                      />
                      </div>

                      <MDBInput
                        label='Your Password'
                        onChange={this.onChangepassword}
                        icon="lock"
                        group
                        type='password'
                        validate
                        labelClass='text-dark font-weight-bold'
                      />

                     <MDBRow className='d-flex align-items-center mb-6'>
                      <div className='text-center mb-3 col-md-12'>
                        <Link to="/playerDetails">
                            <MDBBtn
                              color='dark'
                              rounded
                              type='button'
                              className='btn-block z-depth-1'
                              onClick={this.changeState}
                            >
                            LogIn
                            </MDBBtn>
                        </Link>

                       </div>

                     </MDBRow>
                     <MDBCol md='10'>
                        <p className='font-small text d-flex justify-content-end '>
                          Not Have an account?&nbsp;&nbsp;
                          <a href='/signup' className='black-text ml-54 font-weight-bold'>
                           SignUp
                          </a>
                        </p>
                     </MDBCol>

            </div>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>    
      </div>
      </div>)
      :(
        null
      )
      }
    </div>
  )
 }
}
export default Login;
