import React from 'react'
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBCard, MDBInput } from 'mdbreact';
import "./login.css";
import axios from 'axios';



class Login extends React.Component{
   constructor(props){
     super(props);

     this.state={
       loginOpen:true,
       username:'',
       password:''
     }
   }

   onChangeusername=(event)=>{
     this.setState({username:event.target.value})
   }
   onChangepassword=(event)=>{
     this.setState({password:event.target.value})
   }
   changeState(){
     const User = {
       username:this.state.username,
       password:this.state.password
     }
     axios.post("http://localhost:5000/api/users/login",User).then((data)=>{
       if(data.data.success == true){
         console.log("User is LoggedIn")//need to imporve from here //
         localStorage.setItem("isLoggedIn",true);
         localStorage.setItem("User",User.username);
       }
     })

   }

 render(){
  return(
    <div>
      <div className="Card">
      <MDBContainer>
        <MDBRow>
          <MDBCol md='6'>
                <MDBCard
                  className='card-image'
                   style={{
                     backgroundImage:
                       'url(https://i.pinimg.com/originals/4f/8a/18/4f8a18a2a6f9a3291bee46c24ea9ec5d.jpg)',
                     width: '25rem', 
                     backgroundRepeat: 'no-repeat',
                     backgroundSize: '100%',
                   }}
                >

            <div className='text-white rgba-blue-slight py-5 px-5 z-depth-4'>
                        <div className='text-center'>
                           <h3 className='text-dark mb-5 mt-4 font-weight-bold'>
                              <strong>Log</strong>
                               <a className='purple-text font-weight-bold'>
                               <strong>In</strong>
                               </a>
                           </h3>
                        </div>
                      <div>

                      <MDBInput
                        label='Your Username'
                        onChange={this.onChangeusername}
                        icon='user'
                        group
                        type='text'
                        validate
                        labelClass='text-dark font-weight-bold'
                      />
                      </div>

                      <MDBInput
                        label='Your Password'
                        onChange={this.onChangepassword}
                        icon="lock"
                        group
                        type='password'
                        validate
                        labelClass='text-dark font-weight-bold'
                      />

                     <MDBRow className='d-flex align-items-center mb-6'>
                      <div className='text-center mb-3 col-md-12'>
                          <a href="/PlayerDetails">
                            <MDBBtn
                              color='dark'
                              rounded
                              type='button'
                              className='btn-block z-depth-1'
                            >
                            LogIn
                            </MDBBtn>
                          </a>
                      </div>

                     </MDBRow>
                     <MDBCol md='10'>
                        <p className='font-small text d-flex justify-content-end '>
                          Not Have an account?&nbsp;&nbsp;
                          <a href='/signup' className='black-text ml-54 font-weight-bold'>
                           SignUp
                          </a>
                        </p>
                     </MDBCol>

            </div>
            </MDBCard>
          </MDBCol>
        </MDBRow>
      </MDBContainer>          
      </div>
    </div>
  )
 }
}
export default Login;
