class AYATab extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            teamname:'',
            goalWon:'',
            goalAttmp:'',
            tackleWon:'',
            tackleAttmp:'',
            passesWon:'',
            passesAttmp:'',
            GoalAccuracy:'',
            TackleAccuracy:'',
            PassesAccuracy:'', 
